mapper.select:
select distinct d.object_id, '${object.entityName}' entity_name, d.*



        , sort_a.text
        , sort_a.number
        , sort_a.date
        , sort_v.text







        order by d.id ${asc}
        order by sort_a.text ${asc}
        order by sort_a.number + 0 ${asc}
        order by sort_a.date ${asc}
        order by sort_v.text ${asc}



${limit}
